/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  /* Custom theme options */
  --color-accent: #004700;

  /* Base values */
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 0;
  --border-color: #e0e1e1;
}

/* Root element (use instead of `body`) */
#page {
  font-family: system-ui, sans-serif;
  color: #111;
  line-height: 1.5;
  font-size: 1.125rem;
  background: white;
}

/* Elements */
.section-container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 5rem 2rem;
}

a.link {
  line-height: 1.3;

  border-bottom: 2px solid var(--color-accent);
  transform: translateY(-2px); /* move link back into place */
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-size: 2.5rem;
  line-height: 1.15;

}

.button {
  color: white;
  background: var(--color-accent, rebeccapurple);
  border-radius: 0;
  padding: 18px 24px;
  transition: var(--transition, 0.1s box-shadow);
  border: 0; /* reset */

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent, rebeccapurple);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent, rebeccapurple);
  }
}

/* Content Section */
.content {
  max-width: 900px;
  margin: 0 auto;
  padding: 3rem 2rem;

  p {
    margin-bottom: 1rem;
    line-height: 1.5;
  }

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  a.link {
    line-height: 1.3;
    font-weight: 500;
    border-bottom: 2px solid var(--color-accent);
    transform: translateY(-2px); /* move link back into place */
    transition: var(--transition, 0.1s border);

    &:hover {
      border-color: transparent;
    }
  }

  h1 {
    font-size: 3rem;
    font-weight: 500;
    line-height: 1.1;
    margin-bottom: 1.5rem;
  }

  h2 {
    font-size: 2.5rem;
    font-weight: 500;
    margin-bottom: 1rem;
  }

  h3 {
    font-size: 2rem;
    font-weight: 500;
    margin-bottom: 1rem;
  }

  ul {
    list-style: disc;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  ol {
    list-style: decimal;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  blockquote {
    padding: 2rem;
    margin-top: 1.5rem;
    margin-bottom: 1.5rem;
    border-left: 5px solid var(--color-accent);
  }
}

.section-container {
